SELECT article.*, article_category.name as articleCategoryName, person_user.username as creatorUsername, person_user.buddha as buddha, ( select COUNT(article_comment.id) FROM article_comment WHERE article_comment.article_id = article.id ) as leaveWords FROM article, article_category, person_user ${ew.customSqlSegment}
WHERE article_category.id = article.article_category_id AND article.creator_id = person_user.id
AND article_category.id = article.article_category_id AND article.creator_id = person_user.id

SELECT article.*, article_category.name as articleCategoryName, person_user.username as creatorUsername, person_user.buddha as buddha, ( select COUNT(article_comment.id) FROM article_comment WHERE article_comment.article_id = article.id ) as leaveWords FROM article, article_category, person_user ${ew.customSqlSegment}
WHERE article.id = ${id} AND article_category.id = article.article_category_id AND article.creator_id = person_user.id
AND article.id = ${id} AND article_category.id = article.article_category_id AND article.creator_id = person_user.id
